import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Search for Jun's listing using the search input
    await page.fill('#locationInput', 'Jun');
    await page.click('button.search-btn');
    await page.waitForTimeout(2000);

    // Wait for listings to load and click on jun_2 listing
    await page.waitForSelector('.listing-card');
    const jun2Listing = await page.$('[data-listing-id="jun_2"]');
    if (!jun2Listing) {
      throw new Error('jun_2 listing not found');
    }
    await jun2Listing.click();

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('#modal-checkin', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Fill check-in date (using current date + 7 days)
    const checkinDate = new Date();
    checkinDate.setDate(checkinDate.getDate() + 7);
    const checkinFormatted = checkinDate.toISOString().split('T')[0];
    await page.fill('#modal-checkin', checkinFormatted);

    // Fill check-out date (current date + 14 days)
    const checkoutDate = new Date();
    checkoutDate.setDate(checkoutDate.getDate() + 14);
    const checkoutFormatted = checkoutDate.toISOString().split('T')[0];
    await page.fill('#modal-checkout', checkoutFormatted);

    // Set guests to 2 adults using the guest counter buttons
    await page.click('button:has-text("Add guests")');
    await page.waitForSelector('.guest-menu', { state: 'visible' });

    // Increase adults to 2
    const adultPlusBtn = await page.$('div.guest-option:has-text("Adults") button:has-text("+")');
    await adultPlusBtn.click();
    await adultPlusBtn.click();

    // Close guest menu
    await page.click('button:has-text("Done")');

    // Wait for total cost to update and take screenshot
    await page.waitForFunction(() => {
      const totalCost = document.getElementById('total-cost');
      return totalCost && totalCost.textContent.trim() !== '';
    });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_filled.png'));

    // Extract total cost value
    const totalCost = await page.textContent('#total-cost');

    return {
      success: true,
      action: "Opened jun_2 modal, filled check-in/check-out dates, set guests to 2, and calculated total cost",
      extractedData: {
        checkinDate: checkinFormatted,
        checkoutDate: checkoutFormatted,
        guestCount: 2,
        totalCost: totalCost.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
