import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Find and open Jun's listing (jun_2)
    const junListing = await page.locator('[data-id="jun_2"]').first();
    await junListing.waitFor({ state: 'visible' });
    await junListing.click();

    // Wait for modal to appear and take screenshot
    await page.waitForSelector('#modal-checkin', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));

    // Fill check-in date (using current date + 2 days)
    const checkinDate = new Date();
    checkinDate.setDate(checkinDate.getDate() + 2);
    const checkinFormatted = checkinDate.toISOString().split('T')[0];
    await page.fill('#modal-checkin', checkinFormatted);

    // Fill check-out date (current date + 5 days)
    const checkoutDate = new Date();
    checkoutDate.setDate(checkoutDate.getDate() + 5);
    const checkoutFormatted = checkoutDate.toISOString().split('T')[0];
    await page.fill('#modal-checkout', checkoutFormatted);

    // Set guests to 2 adults
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-option', { state: 'visible' });

    // Increase adults count to 2
    const adultPlusBtn = await page.locator('.guest-option:first-child button:has-text("+")');
    await adultPlusBtn.click();
    await adultPlusBtn.click();

    // Take screenshot after form fill
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_form_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_form_filled.png'));

    // Wait for total cost calculation and extract value
    await page.waitForTimeout(1000); // Allow time for calculation
    const totalCost = await page.textContent('#total-cost');

    // Verify total cost is calculated
    if (!totalCost || totalCost === '$0') {
      throw new Error('Total cost not calculated properly');
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_result.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled reservation form with dates and guest count, and verified total cost calculation",
      extractedData: {
        checkinDate: checkinFormatted,
        checkoutDate: checkoutFormatted,
        guests: 2,
        totalCost: totalCost.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

