    await page.waitForSelector('#total-cost', { timeout: 5000 });
    const totalCost = await page.textContent('#total-cost');

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final_result.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final_result.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled booking form with dates and guests, and extracted total cost",
      extractedData: {
        checkinDate: '2024-06-15',
        checkoutDate: '2024-06-20',
        guests: 2,
        totalCost: totalCost.trim()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click on jun_2 listing
    await page.waitForSelector('[data-id="jun_2"]');
    await page.click('[data-id="jun_2"]');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_jun2_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_jun2_clicked.png'));

    // Wait for modal to appear
    await page.waitForSelector('.booking-form', { state: 'visible' });

    // Fill check-in date
    await page.fill('#modal-checkin', '2024-06-15');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkin_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkin_filled.png'));

    // Fill check-out date
    await page.fill('#modal-checkout', '2024-06-18');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_checkout_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_checkout_filled.png'));

    // Open guest selector
    await page.click('#modal-guests');
    await page.waitForSelector('.guest-menu', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_guest_menu_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_guest_menu_opened.png'));

    // Add 2 adults
    const adultPlus = await page.$('.guest-menu .guest-option:first-child button:has-text("+")');
    await adultPlus.click();
    await adultPlus.click();
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_adults_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_adults_added.png'));

    // Close guest menu by clicking outside
    await page.click('.booking-form');
    await page.waitForSelector('.guest-menu', { state: 'hidden' });

    // Wait for total cost to update
    await page.waitForFunction(() => {
      const totalCost = document.querySelector('#total-cost');
      return totalCost && totalCost.textContent.trim() !== '';
    });

    // Extract total cost
    const totalCost = await page.$eval('#total-cost', el => el.textContent.trim());
    await page.screenshot({ path: path.join(screenshotsDir, 'step_7_total_cost.png') });
    screenshots.push(path.join(screenshotsDir, 'step_7_total_cost.png'));

    return {
      success: true,
      action: "Opened jun_2 modal, filled check-in/check-out dates, set guests to 2 adults, and extracted total cost",
      extractedData: {
        totalCost: totalCost
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
