import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on each category filter and record the number of listings
    const categories = await page.$$('.filter-btn[data-filter]');
    const extractedData = {};

    for (let i = 0; i < categories.length; i++) {
      await categories[i].click();
      await page.waitForTimeout(2000); // Wait for listings to load

      // Take screenshot after clicking each category
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_category_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_category_${i + 1}.png`));

      // Extract the number of listings
      const listings = await page.$$('.listing-card');
      extractedData[`category_${i + 1}`] = listings.length;
    }

    return {
      success: true,
      action: "Clicked on each category filter and recorded the number of listings",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Array to hold the counts of listings for each category
    const categoryCounts = {};

    // Select all filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Iterate over each filter button
    for (const button of filterButtons) {
      // Click the filter button
      await button.click();
      await page.waitForLoadState('networkidle');

      // Take a screenshot after clicking the filter
      const filterName = await button.evaluate(btn => btn.innerText.trim());
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_${filterName}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_${filterName}.png`));

      // Get the count of listings displayed after filter is applied
      const listings = await page.$$('.listing-card');
      categoryCounts[filterName] = listings.length;

      // Optionally, if needed, click back to the initial state
      // await page.goBack();
      // await page.waitForLoadState('networkidle');
    }

    // Return the extracted data
    return {
      success: true,
