import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on each category filter and record the number of listings
    const categories = await page.$$('.filter-btn[data-filter]');
    const extractedData = {};

    for (const category of categories) {
      const categoryName = await category.evaluate(el => el.textContent.trim());
      await category.click();
      await page.waitForLoadState('networkidle');

      // Take a screenshot after clicking the category
      await page.screenshot({ path: path.join(screenshotsDir, `step_category_${categoryName}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_category_${categoryName}.png`));

      // Count the number of listings
      const listings = await page.$$('.listing-card');
      extractedData[categoryName] = listings.length;

      // Click back to the main page or reset the filters if necessary
      // Assuming there's a reset button or we can navigate back
      // await page.click('selector_for_reset_or_back'); // Uncomment and replace with actual selector if needed
    }

    return {
      success: true,
      action: "Clicked on each category filter and recorded the number of listings.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Initialize an object to hold the category counts
    const categoryCounts = {};

    // Select all category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Iterate through each filter button
    for (const button of filterButtons) {
      // Click the filter button
      await button.click();
      await page.waitForTimeout(1000); // Wait for content to load

      // Capture the screenshot after clicking
      const screenshotPath = path.join(screenshotsDir, `step_${categoryCounts.length + 2}_filter_clicked.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);

      // Extract the count of listings after applying the filter
      const listingCountElement = await page.$('.listing-card'); // Change this selector if needed
      const countText = await listingCountElement.evaluate(el => el ? el.innerText : '0');
      const count = parseInt(countText) || 0; // Parse the count, default to 0 if not found

      // Store the count in the object
      const filterName = await button.evaluate(el => el.getAttribute('data-filter'));
      categoryCounts[filterName] = count;
    }

    return {
      success: true,
      action: "Clicked through category filters and recorded listing counts.",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Define an object to store the counts for each category
    const categoryCounts = {};

    // Select all filter buttons and iterate through each
    const filterButtons = await page.$$('.filter-btn[data-filter]');
    for (let button of filterButtons) {
      // Click the filter button
      await button.click();
      await page.waitForLoadState('networkidle');

      // Take a screenshot after clicking the filter button
      const categoryName = await button.evaluate(el => el.dataset.filter);
      await page.screenshot({ path: path.join(screenshotsDir, `step_filtered_${categoryName}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_filtered_${categoryName}.png`));

      // Wait for the listings to load and count them
      await page.waitForSelector('.listing-card', { timeout: 5000 });
      const listings = await page.$$('.listing-card');
      categoryCounts[categoryName] = listings.length;

      // Click back to the main page or reset filters if required
      // Assuming a reset function or navigation to the main section exists
      // await page.click('selector_to_reset_filters'); // Replace with actual selector if needed
    }

    // Return the collected data
    return {
      success: true,
      action: "Filtered through categories and recorded counts of listings.",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
