import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on each category filter and record the number of listings
    const categories = await page.$$('.filter-btn[data-filter]');
    const extractedData = {};

    for (let i = 0; i < categories.length; i++) {
      // Click the category filter
      await categories[i].click();
      await page.waitForLoadState('networkidle');

      // Take a screenshot after clicking the filter
      await page.screenshot({ path: path.join(screenshotsDir, `step_2_category_${i + 1}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_2_category_${i + 1}.png`));

      // Extract the number of listings
      const listingsCount = await page.$$eval('.listing-card', listings => listings.length);
      const filterName = await categories[i].evaluate(el => el.textContent.trim());
      extractedData[filterName] = listingsCount;

      // Optionally, navigate back to the main page or reset filters if necessary
      // await page.goBack(); // Uncomment if needed
      // await page.waitForLoadState('networkidle'); // Wait for the page to load again
    }

    return {
      success: true,
      action: "Clicked on each category filter and recorded the number of listings.",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

