import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Set up an object to store the counts for each category
    const categoryCounts = {};

    // Select all filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Iterate through each filter button
    for (const button of filterButtons) {
      // Click on the filter button
      await button.click();
      await page.waitForTimeout(1000); // Wait for the content to load

      // Count the number of listings
      const listings = await page.$$('.listing-card');
      categoryCounts[await button.evaluate(el => el.getAttribute('data-filter'))] = listings.length;

      // Take a screenshot after applying the filter
      await page.screenshot({ path: path.join(screenshotsDir, `step_filter_${await button.evaluate(el => el.getAttribute('data-filter'))}.png`) });
      screenshots.push(path.join(screenshotsDir, `step_filter_${await button.evaluate(el => el.getAttribute('data-filter'))}.png`));
    }

    return {
      success: true,
      action: "Filtered through all categories and recorded the number of listings for each category.",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
