import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons to load
    await page.waitForSelector('.filter-btn[data-filter]', { timeout: 10000 });

    // Get all category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Object to store category counts
    const categoryCounts = {};

    // Click through each filter button and count listings
    for (let i = 0; i < filterButtons.length; i++) {
      // Re-fetch buttons to avoid stale elements
      const currentButtons = await page.$$('.filter-btn[data-filter]');

      // Get category name from data-filter attribute
      const categoryName = await currentButtons[i].getAttribute('data-filter');

      // Click the filter button
      await currentButtons[i].click();

      // Wait for listings to update
      await page.waitForTimeout(1500);
      await page.waitForSelector('.listing-card', { timeout: 5000 });

      // Count listing cards
      const listings = await page.$$('.listing-card');
      categoryCounts[categoryName] = listings.length;

      // Take screenshot after each filter
      const screenshotPath = path.join(screenshotsDir, `step_2_filter_${i+1}.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked through 8 category filters and recorded listing counts",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons to load
    await page.waitForSelector('.filter-btn[data-filter]', { timeout: 10000 });

    // Get all category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Verify we have 8 categories as expected
    if (filterButtons.length < 8) {
      throw new Error(`Expected 8 filter buttons, found ${filterButtons.length}`);
    }

    const categoryCounts = {};

    // Iterate through each category filter
    for (let i = 0; i < filterButtons.length; i++) {
      // Get the category name from data-filter attribute
      const category = await page.evaluate((el) => el.getAttribute('data-filter'), filterButtons[i]);

      // Click the filter button
      await filterButtons[i].click();

      // Wait for listings to update (using multiple wait strategies)
      await page.waitForTimeout(500); // Brief pause for UI update
      await page.waitForSelector('.listing-card', { state: 'attached' });

      // Count the number of listing cards
      const listingCount = await page.$$eval('.listing-card', (cards) => cards.length);

      // Record the count for this category
      categoryCounts[category] = listingCount;

      // Take screenshot of filtered results
      const screenshotPath = path.join(screenshotsDir, `step_2_${category}_filter.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked all 8 category filters and recorded listing counts",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete category filtering task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

