import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons and listing cards to load
    await page.waitForSelector('.filter-btn[data-filter]', { timeout: 10000 });
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Get all category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Validate we have 8 categories as expected
    if (filterButtons.length !== 8) {
      throw new Error(`Expected 8 filter buttons, found ${filterButtons.length}`);
    }

    const categoryCounts = {};

    // Iterate through each category filter
    for (let i = 0; i < filterButtons.length; i++) {
      // Get category name from data-filter attribute
      const categoryName = await page.evaluate((el) => el.getAttribute('data-filter'), filterButtons[i]);

      // Click the filter button
      await filterButtons[i].click();

      // Wait for content to update
      await page.waitForTimeout(1000);
      await page.waitForSelector('.listing-card', { timeout: 5000 });

      // Count listing cards
      const listingCards = await page.$$('.listing-card');
      const count = listingCards.length;

      // Record the count
      categoryCounts[categoryName] = count;

      // Take screenshot after each filter click
      const screenshotPath = path.join(screenshotsDir, `step_2_filter_${i+1}_${categoryName}.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked 8 category filters and recorded listing counts for each category",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete category filter analysis",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

