import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons and listing cards to load
    await page.waitForSelector('.filter-btn[data-filter]', { timeout: 10000 });
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Get all filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');
    const categoryCounts = {};

    // Click through each filter button sequentially
    for (let i = 0; i < filterButtons.length; i++) {
      const button = filterButtons[i];

      // Get category name from data-filter attribute
      const category = await button.getAttribute('data-filter');

      // Click the filter button
      await button.click();
      await page.waitForTimeout(1000); // Wait for UI update

      // Wait for listings to potentially update
      await page.waitForFunction(() => {
        const cards = document.querySelectorAll('.listing-card');
        return cards.length > 0 || document.querySelector('.no-results');
      }, { timeout: 5000 });

      // Count visible listing cards
      const listingCount = await page.$$eval('.listing-card', cards => cards.length);

      // Record count for this category
      categoryCounts[category] = listingCount;

      // Take screenshot after each filter
      await page.screenshot({ 
        path: path.join(screenshotsDir, `step_2_filter_${category}.png`) 
      });
      screenshots.push(path.join(screenshotsDir, `step_2_filter_${category}.png`));
    }

    return {
      success: true,
      action: "Clicked through all category filters and recorded listing counts",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
