import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons to load
    await page.waitForSelector('.filter-btn[data-filter]', { timeout: 10000 });

    // Get all 8 category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Object to store results
    const categoryCounts = {};

    // Click through each category filter and count listings
    for (let i = 0; i < filterButtons.length; i++) {
      // Re-fetch buttons to avoid stale elements
      const currentButtons = await page.$$('.filter-btn[data-filter]');
      const button = currentButtons[i];

      // Get category name from data-filter attribute
      const category = await button.getAttribute('data-filter');

      // Click the filter button
      await button.click();

      // Wait for listings to update
      await page.waitForTimeout(1000);
      await page.waitForSelector('.listing-card', { timeout: 5000 });

      // Count listings for this category
      const listings = await page.$$('.listing-card');
      categoryCounts[category] = listings.length;

      // Take screenshot after each filter
      const screenshotPath = path.join(screenshotsDir, `step_${i+2}_${category}.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked through 8 category filters and recorded listing counts for each",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

