import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons and listing cards to load
    await page.waitForSelector('.filter-btn[data-filter]');
    await page.waitForSelector('.listing-card');

    // Get all category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Object to store category counts
    const categoryCounts = {};

    // Iterate through each filter button
    for (let i = 0; i < filterButtons.length; i++) {
      // Get filter category name
      const category = await filterButtons[i].getAttribute('data-filter');

      // Click the filter button
      await filterButtons[i].click();
      await page.waitForTimeout(1000); // Wait for filter to apply

      // Count visible listing cards
      const listingCount = await page.$$eval('.listing-card', 
        cards => cards.filter(card => card.offsetParent !== null).length
      );

      // Store count for this category
      categoryCounts[category] = listingCount;

      // Take screenshot after each filter
      await page.screenshot({ 
        path: path.join(screenshotsDir, `step_2_filter_${category}.png`) 
      });
      screenshots.push(path.join(screenshotsDir, `step_2_filter_${category}.png`));
    }

    return {
      success: true,
      action: "Clicked through 8 category filters and recorded listing counts",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete category filtering task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

