    // Object to store category counts
    const categoryCounts = {};

    // Iterate through each filter button
    for (let i = 0; i < filterButtons.length; i++) {
      // Re-locate buttons to avoid stale elements
      const currentButtons = await page.$$('.filter-btn[data-filter]');
      const button = currentButtons[i];

      // Get category name from data-filter attribute
      const category = await button.getAttribute('data-filter');

      // Click the filter button
      await button.click();

      // Wait for content to update
      await page.waitForTimeout(1000);

      // Wait for listing cards to load
      await page.waitForSelector('.listing-card', { timeout: 3000 });

      // Count listing cards
      const listings = await page.$$('.listing-card');
      categoryCounts[category] = listings.length;

      // Take screenshot after each filter
      const screenshotPath = path.join(screenshotsDir, `step_2_filter_${category}.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked through 8 category filters and recorded listing counts for each",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
