import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons and listing cards to load
    await page.waitForSelector('.filter-btn[data-filter]');
    await page.waitForSelector('.listing-card');

    // Get all filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');
    const results = {};

    // Click through each filter button and record listing counts
    for (let i = 0; i < filterButtons.length; i++) {
      // Get filter category name
      const filterName = await page.evaluate(el => el.dataset.filter, filterButtons[i]);

      // Click the filter button
      await filterButtons[i].click();
      await page.waitForTimeout(1000); // Wait for content to update

      // Wait for listings to update
      await page.waitForSelector('.listing-card', { state: 'attached' });

      // Count listing cards
      const listingCount = await page.$$eval('.listing-card', cards => cards.length);

      // Record result
      results[filterName] = listingCount;

      // Take screenshot of filtered results
      const screenshotPath = path.join(screenshotsDir, `step_${i+2}_${filterName}.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked 8 category filters and recorded listing counts for each category",
      extractedData: results,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

