      await page.waitForSelector('.listing-card', { state: 'attached' });

      // Count visible listing cards
      const listingCount = await page.$$eval('.listing-card', cards => 
        cards.filter(card => card.offsetParent !== null).length
      );

      // Record count for this category
      categoryCounts[category] = listingCount;

      // Take screenshot after each filter
      const screenshotPath = path.join(screenshotsDir, `step_${i+2}_${category}.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked all 8 category filters and recorded listing counts",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete category filter task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons to be available
    await page.waitForSelector('.filter-btn[data-filter]', { timeout: 10000 });

    // Get all category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Initialize result object
    const categoryCounts = {};

    // Iterate through each filter button
    for (let i = 0; i < filterButtons.length; i++) {
      try {
        // Re-locate the button to avoid stale element reference
        const currentButtons = await page.$$('.filter-btn[data-filter]');
        const button = currentButtons[i];

        // Get category name from data-filter attribute
        const category = await button.getAttribute('data-filter');

        // Click the filter button
        await button.click();

        // Wait for content to update - using multiple strategies
        await page.waitForTimeout(1000); // Brief pause for UI update
        await page.waitForLoadState('networkidle');

        // Wait for listing cards to be present
        await page.waitForSelector('.listing-card', { timeout: 5000 });

        // Count listing cards
        const listings = await page.$$('.listing-card');
        const count = listings.length;

        // Store the count
        categoryCounts[category] = count;

        // Take screenshot after each filter
        await page.screenshot({ 
          path: path.join(screenshotsDir, `step_2_filter_${category}.png`) 
        });

      } catch (error) {
        console.error(`Error processing filter ${i}:`, error.message);
        categoryCounts[`filter_${i}_error`] = 0;
      }
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Clicked through 8 category filters and recorded listing counts",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ 
      path: path.join(screenshotsDir, 'error.png') 
    });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
