import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons to load
    await page.waitForSelector('.filter-btn[data-filter]');

    // Get all category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Verify we have at least 8 categories as mentioned in task
    if (filterButtons.length < 8) {
      throw new Error(`Expected 8 filter buttons but found ${filterButtons.length}`);
    }

    // Object to store category counts
    const categoryCounts = {};

    // Iterate through each filter button
    for (let i = 0; i < 8; i++) {
      // Get filter data attribute
      const filterValue = await filterButtons[i].getAttribute('data-filter');

      // Click the filter button
      await filterButtons[i].click();

      // Wait for listings to update
      await page.waitForTimeout(1000);
      await page.waitForSelector('.listing-card', { timeout: 5000 });

      // Count listing cards
      const listings = await page.$$('.listing-card');
      categoryCounts[filterValue] = listings.length;

      // Take screenshot after each filter
      const screenshotPath = path.join(screenshotsDir, `step_2_filter_${i+1}.png`);
      await page.screenshot({ path: screenshotPath });
      screenshots.push(screenshotPath);
    }

    return {
      success: true,
      action: "Clicked 8 category filters and recorded listing counts",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

