import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for filter buttons to be available
    await page.waitForSelector('.filter-btn[data-filter]', { timeout: 5000 });

    // Get all category filter buttons
    const filterButtons = await page.$$('.filter-btn[data-filter]');

    // Object to store category counts
    const categoryCounts = {};

    // Iterate through each filter button
    for (let i = 0; i < filterButtons.length; i++) {
      // Get filter category name
      const filterName = await filterButtons[i].getAttribute('data-filter');

      // Click the filter button
      await filterButtons[i].click();

      // Wait for content to update
      await page.waitForTimeout(1000);

      // Wait for listing cards to be visible
      await page.waitForSelector('.listing-card', { state: 'visible', timeout: 3000 });

      // Count visible listing cards
      const listingCount = await page.$$eval('.listing-card', 
        cards => cards.filter(card => card.offsetParent !== null).length
      );

      // Store count in object
      categoryCounts[filterName] = listingCount;

      // Take screenshot after each filter
      await page.screenshot({ 
        path: path.join(screenshotsDir, `step_2_filter_${filterName}.png`) 
      });
      screenshots.push(path.join(screenshotsDir, `step_2_filter_${filterName}.png`));
    }

    return {
      success: true,
      action: "Clicked all 8 category filters and recorded listing counts",
      extractedData: categoryCounts,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
