import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Input 'Gangnam' into the search field
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Gangnam');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_input_gangnam.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_input_gangnam.png'));

    // Step 2: Click the search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_click_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_click_search.png'));

    // Step 3: Apply the luxury filter
    await page.waitForSelector("[data-filter='luxury']");
    await page.click("[data-filter='luxury']");
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_apply_luxury_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_apply_luxury_filter.png'));

    // Step 4: Select the listing with id 'jun_2'
    await page.waitForSelector("[data-listing-id='jun_2']");
    await page.click("[data-listing-id='jun_2']");
    await page.waitForSelector('.modal-overlay'); // Wait for the modal to appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_select_jun_2.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_select_jun_2.png'));

    // Final verification step
    const modalVisible = await page.isVisible('.modal-overlay');
    if (modalVisible) {
      return {
        success: true,
        action: "Successfully searched for 'Gangnam', applied luxury filter, and opened the modal for listing 'jun_2'.",
        extractedData: { modalVisible: true },
        screenshots,
        error: null
      };
    } else {
      throw new Error("Modal did not appear after selecting the listing.");
    }

  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

