import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Input 'Gangnam' into the location input field
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Gangnam');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_input.png'));

    // Step 2: Click the search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_clicked.png'));

    // Step 3: Apply the luxury filter
    await page.waitForSelector("[data-filter='luxury']");
    await page.click("[data-filter='luxury']");
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_luxury_filter_applied.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_luxury_filter_applied.png'));

    // Step 4: Select the listing with ID 'jun_2'
    await page.waitForSelector("[data-listing-id='jun_2']");
    await page.click("[data-listing-id='jun_2']");
    await page.waitForSelector('.modal-overlay'); // Wait for the modal to appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_listing_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_listing_selected.png'));

    // Final verification step
    const modalVisible = await page.isVisible('.modal-overlay');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_modal_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_modal_visible.png'));

    return {
      success: modalVisible,
      action: modalVisible ? "Successfully opened the modal for listing 'jun_2'" : "Failed to open the modal for listing 'jun_2'",
      extractedData: { modalVisible },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

