import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Input 'Gangnam' into the search field
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Gangnam');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_input.png'));

    // Step 2: Click the search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_clicked.png'));

    // Step 3: Apply luxury filter
    await page.waitForSelector("[data-filter='luxury']");
    await page.click("[data-filter='luxury']");
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_luxury_filter_applied.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_luxury_filter_applied.png'));

    // Step 4: Select the listing with id 'jun_2'
    await page.waitForSelector("[data-listing-id='jun_2']");
    await page.click("[data-listing-id='jun_2']");
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_listing_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_listing_selected.png'));

    // Step 5: Wait for the modal to open
    await page.waitForSelector('.modal-overlay');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_modal_opened.png'));

    return {
      success: true,
      action: "Successfully searched for 'Gangnam', applied luxury filter, selected listing 'jun_2', and opened the modal.",
      extractedData: {},
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

