    await page.waitForSelector('.modal-overlay'); // wait for the modal to appear
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_listing_jun_2_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_listing_jun_2_clicked.png'));

    // Step 5: Verify that the modal is open
    const isModalOpen = await page.isVisible('.modal-overlay');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_modal_open_verification.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_modal_open_verification.png'));

    return {
      success: true,
      action: isModalOpen ? "Successfully opened modal for listing 'jun_2'" : "Modal did not open",
      extractedData: { modalOpen: isModalOpen },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
