import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Fill location input with 'Gangnam'
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Gangnam');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_filled.png'));

    // Step 2: Click search button (using multiple selector strategies)
    const searchBtn = await page.$('.search-btn') || await page.$('button:has-text("Search")');
    if (searchBtn) {
      await searchBtn.click();
    } else {
      // Alternative search trigger if button not found
      await page.keyboard.press('Enter');
    }
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_clicked.png'));

    // Step 3: Apply luxury filter
    await page.waitForSelector('[data-filter="luxury"]');
    await page.click('[data-filter="luxury"]');
    await page.waitForTimeout(1500);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_luxury_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_luxury_filter.png'));

    // Step 4: Find and click jun_2 listing
    await page.waitForSelector('[data-listing-id="jun_2"]');
    await page.click('[data-listing-id="jun_2"]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_jun2_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_jun2_selected.png'));

    // Step 5: Wait for modal to appear and verify
    await page.waitForSelector('.modal-overlay', { timeout: 5000 });
    const modalVisible = await page.isVisible('.modal-overlay');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_modal_opened.png'));

    // Extract data from modal
    const modalTitle = await page.$eval('.modal-overlay h1, .modal-overlay h2, .modal-overlay h3', el => el.textContent.trim()).catch(() => 'Not found');
    const modalPrice = await page.$eval('.modal-overlay .price', el => el.textContent.trim()).catch(() => 'Not found');

    return {
      success: modalVisible,
      action: "Searched 'Gangnam', applied luxury filter, selected jun_2 listing, and opened modal",
      extractedData: {
        modalTitle,
        modalPrice,
        stepsCompleted: {
          locationInput: true,
          searchExecuted: true,
          luxuryFilter: true,
          jun2Selected: true,
          modalOpened: modalVisible
        }
      },
      screenshots,
      error: modalVisible ? null : 'Modal did not open successfully'
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

