export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Enter 'Gangnam' in location input
    await page.waitForSelector('#locationInput', { state: 'visible' });
    await page.fill('#locationInput', 'Gangnam');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_filled.png'));

    // Step 2: Click search button
    await page.waitForSelector('.search-btn', { state: 'visible' });
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_clicked.png'));

    // Step 3: Apply luxury filter
    await page.waitForSelector('[data-filter="luxury"]', { state: 'visible' });
    await page.click('[data-filter="luxury"]');
    await page.waitForTimeout(1000); // Wait for filter to apply
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_luxury_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_luxury_filter.png'));

    // Step 4: Find and click jun_2 listing
    await page.waitForSelector('[data-listing-id="jun_2"]', { state: 'visible' });
    await page.click('[data-listing-id="jun_2"]');
    await page.waitForTimeout(1000); // Wait for modal to open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_jun_2_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_jun_2_selected.png'));

    // Step 5: Verify modal is open
    const modalVisible = await page.isVisible('.modal-overlay');
    if (!modalVisible) {
      throw new Error('Modal did not open after selecting jun_2');
    }
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_modal_open.png'));

    // Extract data for verification
    const extractedData = {
      locationInputValue: await page.inputValue('#locationInput'),
      luxuryFilterApplied: await page.isVisible('[data-filter="luxury"].active'),
      jun2Selected: await page.isVisible('[data-listing-id="jun_2"].selected'),
      modalOpen: modalVisible
    };

    return {
      success: true,
      action: "Successfully searched 'Gangnam', applied luxury filter, selected jun_2, and opened modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('domcontentloaded');
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Enter 'Gangnam' in location input
