    const searchBtn = await page.$('.search-btn') || await page.$('button:has-text("Search")');
    if (searchBtn) {
      await searchBtn.click();
    } else {
      throw new Error('Search button not found');
    }

    // Step 3: Wait for results and apply luxury filter
    await page.waitForTimeout(1000); // Wait for results to load
    await page.waitForSelector('[data-filter="luxury"]', { state: 'visible' });
    await page.click('[data-filter="luxury"]');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_luxury_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_luxury_filter.png'));

