    await page.click('[data-listing-id="jun_2"]');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_jun_2_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_jun_2_selected.png'));

    // Step 5: Wait for modal to appear
    await page.waitForSelector('.modal-overlay', { state: 'visible', timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_modal_opened.png'));

    // Extract verification data
    const modalVisible = await page.isVisible('.modal-overlay');
    const listingTitle = await page.textContent('.modal-content h2').catch(() => 'Not found');

    return {
      success: true,
      action: "Successfully searched 'Gangnam', applied luxury filter, selected jun_2 listing, and opened modal",
      extractedData: {
        modalOpened: modalVisible,
        listingTitle: listingTitle,
