import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const screenshots = [];
  let extractedData = {};

  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Enter 'Gangnam' in location input
    await page.waitForSelector('#locationInput', { state: 'visible' });
    await page.fill('#locationInput', 'Gangnam');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_filled.png'));

    // Step 2: Click search button
    await page.waitForSelector('.search-btn', { state: 'visible' });
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_clicked.png'));

    // Step 3: Apply luxury filter
    await page.waitForSelector('[data-filter="luxury"]', { state: 'visible' });
    await page.click('[data-filter="luxury"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_luxury_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_luxury_filter.png'));

    // Step 4: Select jun_2 listing
    await page.waitForSelector('[data-listing-id="jun_2"]', { state: 'visible' });
    await page.click('[data-listing-id="jun_2"]');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_jun2_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_jun2_selected.png'));

    // Step 5: Wait for modal to appear and verify
    await page.waitForSelector('.modal-overlay', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_modal_opened.png'));

    // Extract modal data for verification
    extractedData = {
      modalVisible: await page.isVisible('.modal-overlay'),
      modalTitle: await page.textContent('.modal-header h2').catch(() => 'N/A'),
      modalPrice: await page.textContent('.price-display').catch(() => 'N/A')
    };

    return {
      success: true,
      action: "Successfully searched 'Gangnam', applied luxury filter, selected jun_2 listing, and opened modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'step_error.png') });
    screenshots.push(path.join(screenshotsDir, 'step_error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

