    await page.waitForSelector('.modal-overlay', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_modal_opened.png'));

    // Extract modal data for verification
    const modalData = await page.$eval('.modal-overlay', (modal) => {
      return {
        visible: modal.checkVisibility(),
        title: modal.querySelector('h2')?.textContent || '',
        content: modal.querySelector('.modal-content')?.textContent || ''
      };
    });

