      location: 'Gangnam',
      filterApplied: 'luxury',
      listingSelected: 'jun_2',
      modalVisible: modalData.visible,
      modalTitle: modalData.title,
      modalContent: modalData.content
    };

    return {
      success: true,
      action: "Successfully searched 'Gangnam', applied luxury filter, selected jun_2 listing, and opened modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take final screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error_final_state.png') });
    screenshots.push(path.join(screenshotsDir, 'error_final_state.png'));

    return {
      success: false,
      action: "Failed to complete automation task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
