import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Search for 'Gangnam'
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Gangnam');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_filled.png'));

    // Step 2: Click search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_initiated.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_initiated.png'));

    // Step 3: Apply luxury filter
    await page.waitForSelector('[data-filter="luxury"]');
    await page.click('[data-filter="luxury"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_luxury_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_luxury_filter.png'));

    // Step 4: Select jun_2 listing
    await page.waitForSelector('[data-listing-id="jun_2"]');
    await page.click('[data-listing-id="jun_2"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_jun_2_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_jun_2_selected.png'));

    // Step 5: Wait for modal and verify
    await page.waitForSelector('.modal-overlay', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_modal_opened.png'));

    // Extract modal data for verification
    const modalTitle = await page.$eval('.modal-overlay h1', el => el.textContent.trim());
    const modalPrice = await page.$eval('.modal-overlay .price', el => el.textContent.trim());

    return {
      success: true,
      action: "Successfully searched 'Gangnam', applied luxury filter, selected jun_2 listing, and opened modal",
      extractedData: {
        modalTitle,
        modalPrice
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  const steps = {
    navigation: false,
    searchInput: false,
    searchExecution: false,
    luxuryFilter: false,
    jun2Selection: false,
    modalOpened: false
  };

  const screenshots = [];

  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');
    steps.navigation = true;

    // Take initial screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Fill location input with 'Gangnam'
    await page.waitForSelector('#locationInput', { timeout: 5000 });
    await page.fill('#locationInput', 'Gangnam');
    steps.searchInput = true;

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_filled.png'));

    // Step 2: Click search button
    await page.waitForSelector('.search-btn', { timeout: 5000 });
    await page.click('.search-btn');
    steps.searchExecution = true;

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_clicked.png'));

    // Step 3: Wait for results and apply luxury filter
    await page.waitForSelector('[data-filter="luxury"]', { timeout: 10000 });
    await page.click('[data-filter="luxury"]');
    steps.luxuryFilter = true;

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_luxury_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_luxury_filter.png'));

    // Step 4: Wait for filtered results and select jun_2 listing
    await page.waitForSelector('[data-listing-id="jun_2"]', { timeout: 10000 });
    await page.click('[data-listing-id="jun_2"]');
    steps.jun2Selection = true;

    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_jun2_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_jun2_selected.png'));

    // Step 5: Wait for modal to appear
    await page.waitForSelector('.modal-overlay', { timeout: 10000 });
    steps.modalOpened = true;

    await page.screenshot({ path: path.join(screenshotsDir, 'step_6_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_6_modal_opened.png'));

    // Extract modal title to verify success
    const modalTitle = await page.$eval('.modal-overlay h2', el => el.textContent.trim());

    return {
      success: true,
      action: "Successfully searched 'Gangnam', applied luxury filter, selected jun_2 listing, and opened modal",
      extractedData: {
        steps: steps,
        modalTitle: modalTitle
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take final screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'step_error_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_error_final.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: { steps },
      screenshots,
      error: error.message
    };
  }
}

