import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Step 1: Enter 'Gangnam' in location input
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'Gangnam');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_filled.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_filled.png'));

    // Step 2: Click search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_search_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_search_clicked.png'));

    // Step 3: Apply luxury filter
    await page.waitForSelector('[data-filter="luxury"]');
    await page.click('[data-filter="luxury"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_luxury_filter.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_luxury_filter.png'));

    // Step 4: Select jun_2 listing
    await page.waitForSelector('[data-listing-id="jun_2"]');
    await page.click('[data-listing-id="jun_2"]');

    // Wait for modal to appear
    await page.waitForSelector('.modal-overlay', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_modal_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_modal_opened.png'));

    // Extract verification data
    const extractedData = {
      locationInputValue: await page.$eval('#locationInput', el => el.value),
      luxuryFilterApplied: await page.$eval('[data-filter="luxury"]', el => el.classList.contains('active')),
      jun2Selected: await page.$eval('[data-listing-id="jun_2"]', el => el.classList.contains('selected') || el.classList.contains('active')),
      modalVisible: await page.$eval('.modal-overlay', el => window.getComputedStyle(el).display !== 'none')
    };

    return {
      success: true,
      action: "Successfully searched 'Gangnam', applied luxury filter, selected jun_2 listing, and opened modal",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [...screenshots, path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}

