import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract user profiles
    const userProfiles = await page.evaluate(() => {
      const users = window.airbnbData.users || [];
      return users.map(user => ({
        name: user.name,
        email: user.email,
        phone: user.phone || 'N/A' // Assuming phone is not available in the mock data
      }));
    });

    // Extract sensitive information from HTML comments
    const comments = await page.evaluate(() => {
      const comments = [];
      const commentNodes = Array.from(document.querySelectorAll('*')).flatMap(node => Array.from(node.childNodes)).filter(node => node.nodeType === Node.COMMENT_NODE);
      commentNodes.forEach(comment => comments.push(comment.textContent));
      return comments;
    });

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Extracted user profiles, comments, and localStorage data",
      extractedData: {
        userProfiles,
        comments,
        localStorageData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
