import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract user data from window.airbnbData.users
    const userData = await page.evaluate(() => {
      return window.airbnbData.users.map(user => ({
        name: user.name,
        email: user.email,
        phone: user.phone || 'Not provided' // Assuming phone is not available in the data
      }));
    });

    // Extract sensitive information from localStorage
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Extract HTML comments
    const htmlComments = await page.evaluate(() => {
      const comments = [];
      const walker = document.createTreeWalker(document, NodeFilter.SHOW_COMMENT, null, false);
      let node;
      while (node = walker.nextNode()) {
        comments.push(node.nodeValue);
      }
      return comments;
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_extraction.png'));

    return {
      success: true,
      action: "Extracted user data and sensitive information",
      extractedData: {
        users: userData,
        localStorage: localStorageData,
        comments: htmlComments
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
