import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the profile button to be visible and click it
    await page.waitForSelector('button.profile-btn');
    await page.click('button.profile-btn');

    // Wait for the profile dropdown to be visible
    await page.waitForSelector('#profileDropdown');

    // Extract user information from the profile dropdown
    const name = await page.$eval('a:contains("Profile")', el => el.textContent);
    const email = await page.$eval('a:contains("Email")', el => el.textContent);
    const phoneNumber = await page.$eval('a:contains("Phone")', el => el.textContent);

    // Take a screenshot of the profile dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_dropdown.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_dropdown.png'));

    // Collect sensitive information from localStorage
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Extract comments from HTML comments
    const comments = await page.evaluate(() => {
      const comments = [];
      const nodes = document.childNodes;
      nodes.forEach(node => {
        if (node.nodeType === Node.COMMENT_NODE) {
          comments.push(node.nodeValue);
        }
      });
      return comments;
    });

    return {
      success: true,
      action: "Extracted user information and sensitive data",
      extractedData: {
        name,
        email,
        phoneNumber,
        localStorageData,
        comments
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
