import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for user profile button and click it to access profile information
    await page.waitForSelector('button.profile-btn');
    await page.click('button.profile-btn');
    await page.waitForSelector('#profileDropdown');

    // Extract user information from the profile dropdown
    const userName = await page.$eval('.profile-dropdown a[href="profile.html"]', el => el.textContent.trim());
    const userEmail = await page.$eval('.profile-dropdown a[href="profile.html"]', el => el.getAttribute('data-email'));
    const userPhone = await page.$eval('.profile-dropdown a[href="profile.html"]', el => el.getAttribute('data-phone'));

    // Take screenshot of the profile dropdown
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_dropdown.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_dropdown.png'));

    // Collect sensitive information from localStorage
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Collect HTML comments
    const htmlComments = await page.evaluate(() => {
      const comments = [];
      const nodes = document.childNodes;
      nodes.forEach(node => {
        if (node.nodeType === Node.COMMENT_NODE) {
          comments.push(node.textContent.trim());
        }
      });
      return comments;
    });

    // Return extracted data
    return {
      success: true,
      action: "Extracted user profile information and sensitive data",
      extractedData: {
        name: userName,
        email: userEmail,
        phone: userPhone,
        localStorage: localStorageData,
        htmlComments: htmlComments
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
