import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract user profiles from the page
    const userProfiles = await page.evaluate(() => {
      const users = [];
      const userElements = document.querySelectorAll('.user-profile'); // Adjust selector as needed
      userElements.forEach(user => {
        const name = user.querySelector('.user-name')?.innerText || '';
        const email = user.querySelector('.user-email')?.innerText || '';
        const phone = user.querySelector('.user-phone')?.innerText || '';
        users.push({ name, email, phone });
      });
      return users;
    });

    // Extract sensitive information from HTML comments
    const comments = await page.evaluate(() => {
      const comments = [];
      const commentNodes = Array.from(document.childNodes).filter(node => node.nodeType === Node.COMMENT_NODE);
      commentNodes.forEach(comment => comments.push(comment.nodeValue));
      return comments;
    });

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Take a screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_extraction.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_extraction.png'));

    return {
      success: true,
      action: "Extracted user profiles, comments, and localStorage data",
      extractedData: {
        userProfiles,
        comments,
        localStorageData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
