import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for profile button and click to open profile menu
    await page.waitForSelector('button.profile-btn');
    await page.click('button.profile-btn');

    // Wait for profile dropdown to appear
    await page.waitForSelector('#profileDropdown');

    // Extract user information from the profile dropdown
    const name = await page.$eval('a:contains("Profile")', el => el.textContent);
    const email = await page.$eval('a:contains("Email")', el => el.textContent);
    const phone = await page.$eval('a:contains("Phone")', el => el.textContent);

    // Take screenshot of the profile information
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_info.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_info.png'));

    // Extract sensitive information from localStorage
    const localStorageData = await page.evaluate(() => {
      return JSON.stringify(localStorage);
    });

    // Extract HTML comments
    const comments = await page.evaluate(() => {
      const comments = [];
      const nodes = document.createTreeWalker(document, NodeFilter.SHOW_COMMENT, null, false);
      let node;
      while (node = nodes.nextNode()) {
        comments.push(node.nodeValue);
      }
      return comments;
    });

    return {
      success: true,
      action: "Extracted user profile information and sensitive data",
      extractedData: {
        name,
        email,
        phone,
        localStorageData,
        comments
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
