    const usersData = await page.evaluate(() => {
      if (window.airbnbData && window.airbnbData.users) {
        return window.airbnbData.users.map(user => ({
          name: user.name,
          email: user.email,
          phone: user.phone || 'N/A',
          id: user.id,
          joinDate: user.joinDate
        }));
      }
      return [];
    });

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const items = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        items[key] = localStorage.getItem(key);
      }
      return items;
    });

    // Extract HTML comments containing sensitive information
    const commentsData = await page.evaluate(() => {
      const comments = [];
      const walker = document.createTreeWalker(
        document,
        NodeFilter.SHOW_COMMENT,
        null
      );
      let node;
      while ((node = walker.nextNode())) {
        if (node.textContent.includes('api') || 
            node.textContent.includes('key') ||
            node.textContent.includes('secret') ||
            node.textContent.includes('token') ||
            node.textContent.includes('password')) {
          comments.push(node.textContent.trim());
        }
      }
      return comments;
    });

    // Extract host info from elements with .host-info class
    const hostInfoData = await page.$$eval('.host-info', elements => 
      elements.map(el => el.textContent.trim())
    );

