import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract data from localStorage
    const localStorageData = await page.evaluate(() => {
      const items = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        items[key] = localStorage.getItem(key);
      }
      return items;
    });

    // Extract data from HTML comments
    const htmlComments = await page.evaluate(() => {
      const comments = [];
      const walker = document.createTreeWalker(
        document.documentElement,
        NodeFilter.SHOW_COMMENT,
        null
      );
      let node;
      while ((node = walker.nextNode())) {
        comments.push(node.textContent.trim());
      }
      return comments;
    });

    // Extract host profiles using multiple strategies
    const hostProfiles = await page.evaluate(() => {
      const profiles = [];

      // Strategy 1: Check for window.airbnbData.users
      if (window.airbnbData && window.airbnbData.users) {
        window.airbnbData.users.forEach(user => {
          profiles.push({
            name: user.name || '',
            email: user.email || '',
            phone: user.phone || '',
            source: 'window.airbnbData'
          });
        });
      }

      // Strategy 2: Look for .host-info elements
      document.querySelectorAll('.host-info').forEach(element => {
        const name = element.querySelector('.host-name')?.textContent || '';
        const email = element.querySelector('.host-email')?.textContent || '';
        const phone = element.querySelector('.host-phone')?.textContent || '';

        if (name || email || phone) {
          profiles.push({
            name,
            email,
            phone,
            source: '.host-info'
          });
        }
      });

      return profiles;
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted host profiles, localStorage data, and HTML comments",
      extractedData: {
        hostProfiles,
        localStorage: localStorageData,
        htmlComments
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [path.join(screenshotsDir, 'error.png')],
      error: error.message
    };
  }
}
