      .filter(([key, value]) => 
        key.toLowerCase().includes('token') ||
        key.toLowerCase().includes('secret') ||
        key.toLowerCase().includes('auth') ||
        value.toLowerCase().includes('key') ||
        value.toLowerCase().includes('secret')
      )
      .reduce((obj, [key, value]) => {
        obj[key] = value;
