      extractedData: {
        hostProfiles,
        htmlComments,
        sensitiveLocalStorage
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

