import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract data from window.airbnbData.users
    const userData = await page.evaluate(() => {
      if (window.airbnbData && window.airbnbData.users) {
        return window.airbnbData.users.map(user => ({
          name: user.name,
          email: user.email,
          phone: user.phone || 'N/A'
        }));
      }
      return [];
    });

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const data = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        data[key] = localStorage.getItem(key);
      }
      return data;
    });

    // Extract HTML comments
    const comments = await page.evaluate(() => {
      const commentNodes = [];
      const walker = document.createTreeWalker(
        document,
        NodeFilter.SHOW_COMMENT,
        null,
        false
      );
      let node;
      while ((node = walker.nextNode())) {
        commentNodes.push(node.nodeValue.trim());
      }
      return commentNodes.filter(comment => comment.length > 0);
    });

    // Extract host info from elements
    const hostInfo = await page.$$eval('.host-info', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted host profiles, localStorage data, and HTML comments",
      extractedData: {
        users: userData,
        localStorage: localStorageData,
        comments: comments,
        hostInfo: hostInfo
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
