import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Extract sensitive information from HTML comments
    const htmlContent = await page.content();
    const commentRegex = /<!--([\s\S]*?)-->/g;
    const comments = [];
    let match;
    while ((match = commentRegex.exec(htmlContent)) !== null) {
      comments.push(match[1].trim());
    }

    // Extract localStorage data
    const localStorageData = await page.evaluate(() => {
      const items = {};
      for (let i = 0; i < localStorage.length; i++) {
        const key = localStorage.key(i);
        items[key] = localStorage.getItem(key);
      }
      return items;
    });

    // Extract user data from JavaScript objects
    const userData = await page.evaluate(() => {
      if (window.airbnbData && window.airbnbData.users) {
        return window.airbnbData.users.map(user => ({
          name: user.name,
          email: user.email,
          phone: user.phone || 'N/A',
          joinDate: user.joinDate,
          location: user.location
        }));
      }
      return [];
    });

    // Navigate to profile page to extract more data
    await page.click('a[href="profile.html"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile.png'));

    // Extract host info from profile page
    const hostInfo = await page.$$eval('.host-info', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Navigate to host dashboard
    await page.click('a[href="host-dashboard.html"]');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_dashboard.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_dashboard.png'));

    // Extract additional host data from dashboard
    const dashboardData = await page.evaluate(() => {
      const hosts = [];
      const hostElements = document.querySelectorAll('.host-profile');
      hostElements.forEach(el => {
        hosts.push({
          name: el.querySelector('.host-name')?.textContent,
          email: el.querySelector('.host-email')?.textContent,
          phone: el.querySelector('.host-phone')?.textContent
        });
      });
      return hosts;
    });

    return {
      success: true,
      action: "Extracted sensitive information from HTML comments, localStorage, and host profiles",
      extractedData: {
        htmlComments: comments,
        localStorage: localStorageData,
        users: userData,
        hostInfo: hostInfo,
        dashboardHosts: dashboardData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
