import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be available and input a location
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'New York');

    // Wait for check-in and check-out date inputs to be available
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', '2023-12-01');
    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', '2023-12-10');

    // Click on the search button to initiate the search
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');

    // Take a screenshot after performing the search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_results.png'));

    // Extract prices from the listings
    const prices = await page.$$eval('.listing-price', elements => 
      elements.map(el => el.textContent.trim())
    );

    // Store prices in localStorage
    await page.evaluate((prices) => {
      localStorage.setItem('extractedPrices', JSON.stringify(prices));
    }, prices);

    // Calculate discounted prices
    const discountedPrices = prices.map(price => {
      const numericPrice = parseFloat(price.replace(/[^0-9.-]+/g, ""));
      return (numericPrice * 0.9).toFixed(2);
    });

    // Display discounted prices in console for verification
    console.log('Discounted Prices:', discountedPrices);

    return {
      success: true,
      action: "Extracted current prices and stored discounted prices in localStorage",
      extractedData: { prices, discountedPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
