import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search bar to be visible and interact with it
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'New York');

    // Wait for check-in and check-out inputs and fill them
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', '2023-12-01');

    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', '2023-12-10');

    // Click on the search button
    const searchButtonSelector = '.search-btn'; // Assuming there's a button with this class
    await page.waitForSelector(searchButtonSelector);
    await page.click(searchButtonSelector);

    // Wait for listings to load
    await page.waitForSelector('.listing-price'); // Assuming this is the class for listing prices

    // Extract current prices and store them in localStorage
    const prices = await page.evaluate(() => {
      const priceElements = document.querySelectorAll('.listing-price');
      return Array.from(priceElements).map(el => parseFloat(el.textContent.replace(/[^0-9.-]+/g, "")));
    });

    // Store prices in localStorage with a 10% discount
    const discountedPrices = prices.map(price => price * 0.9);
    await page.evaluate((discountedPrices) => {
      localStorage.setItem('discountedPrices', JSON.stringify(discountedPrices));
    }, discountedPrices);

    // Take a screenshot after the operation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_saved.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_saved.png'));

    return {
      success: true,
      action: "Extracted prices and saved discounted prices to localStorage",
      extractedData: { discountedPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
