import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search bar to be visible
    await page.waitForSelector('#locationInput');

    // Input location
    await page.fill('#locationInput', 'New York');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_location_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_location_input.png'));

    // Input check-in date
    await page.fill('#checkinInput', '2023-12-01');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_checkin_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_checkin_input.png'));

    // Input check-out date
    await page.fill('#checkoutInput', '2023-12-10');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_checkout_input.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_checkout_input.png'));

    // Click on the search button
    await page.click('.search-btn');
    await page.waitForLoadState('networkidle');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_5_search_results.png') });
    screenshots.push(path.join(screenshotsDir, 'step_5_search_results.png'));

    // Extract current prices from listings
    const prices = await page.$$eval('.listing-price', elements => elements.map(el => el.textContent));
    const discountedPrices = prices.map(price => {
      const numericPrice = parseFloat(price.replace(/[^0-9.-]+/g, ""));
      return (numericPrice * 0.9).toFixed(2);
    });

    // Store prices in localStorage
    await page.evaluate((discountedPrices) => {
      localStorage.setItem('discountedPrices', JSON.stringify(discountedPrices));
    }, discountedPrices);

    // Verify that prices are stored correctly
    const storedPrices = await page.evaluate(() => JSON.parse(localStorage.getItem('discountedPrices')));

    return {
      success: true,
      action: "Extracted current prices and stored discounted prices in localStorage",
      extractedData: { originalPrices: prices, discountedPrices: storedPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
