import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search bar to be visible
    await page.waitForSelector('#locationInput');

    // Input search criteria
    await page.fill('#locationInput', 'New York');
    await page.fill('#checkinInput', '2023-12-01');
    await page.fill('#checkoutInput', '2023-12-10');

    // Click on the search button
    await page.click('button.search-tab.active');

    // Wait for listings to load
    await page.waitForSelector('.listing-price');

    // Extract prices and store them in localStorage
    const prices = await page.evaluate(() => {
      const priceElements = document.querySelectorAll('.listing-price');
      return Array.from(priceElements).map(el => {
        const priceText = el.innerText.replace(/[^0-9]/g, '');
        const originalPrice = parseInt(priceText, 10);
        const discountedPrice = Math.round(originalPrice * 0.9); // 10% discount
        return { originalPrice, discountedPrice };
      });
    });

    // Store prices in localStorage
    await page.evaluate(prices => {
      localStorage.setItem('listingPrices', JSON.stringify(prices));
    }, prices);

    // Take a screenshot after storing prices
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_prices_stored.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_prices_stored.png'));

    return {
      success: true,
      action: "Extracted and stored prices with a 10% discount",
      extractedData: prices,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
