import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be available and fill it
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'New York');

    // Wait for the check-in and check-out date inputs and fill them
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', '2023-12-01');
    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', '2023-12-10');

    // Click the search button
    await page.waitForSelector('.search-btn');
    await page.click('.search-btn');

    // Wait for the listings to load
    await page.waitForSelector('.listing-price');

    // Extract prices of all listings
    const prices = await page.$$eval('.listing-price', elements => elements.map(el => el.textContent));

    // Store prices in localStorage
    await page.evaluate((prices) => {
      localStorage.setItem('listingPrices', JSON.stringify(prices));
    }, prices);

    // Calculate discounted prices and log them
    const discountedPrices = prices.map(price => {
      const numericPrice = parseFloat(price.replace(/[^0-9.-]+/g, ""));
      return (numericPrice * 0.9).toFixed(2);
    });

    console.log('Discounted Prices:', discountedPrices);

    // Take a screenshot after the task is completed
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_search.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_search.png'));

    return {
      success: true,
      action: "Extracted prices and stored them in localStorage with discounts applied",
      extractedData: {
        originalPrices: prices,
        discountedPrices: discountedPrices
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
