import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search input to be available and fill it
    await page.waitForSelector('#locationInput');
    await page.fill('#locationInput', 'New York');

    // Wait for the check-in and check-out inputs to be available and fill them
    await page.waitForSelector('#checkinInput');
    await page.fill('#checkinInput', '2023-12-01');

    await page.waitForSelector('#checkoutInput');
    await page.fill('#checkoutInput', '2023-12-10');

    // Click on the search button
    const searchButtonSelector = '.search-btn';
    await page.waitForSelector(searchButtonSelector);
    await page.click(searchButtonSelector);

    // Wait for the listings to load
    await page.waitForSelector('.listing-price');

    // Extract prices and store them in localStorage
    const prices = await page.evaluate(() => {
      const priceElements = document.querySelectorAll('.listing-price');
      return Array.from(priceElements).map(el => parseFloat(el.textContent.replace(/[^0-9.-]+/g, "")));
    });

    // Store prices in localStorage with a 10% discount
    await page.evaluate((prices) => {
      const discountedPrices = prices.map(price => price * 0.9);
      localStorage.setItem('discountedPrices', JSON.stringify(discountedPrices));
    }, prices);

    // Take a final screenshot after the operation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Extracted current prices and stored discounted prices in localStorage",
      extractedData: { discountedPrices: prices.map(price => price * 0.9) },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
