import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the search bar to be available
    await page.waitForSelector('#locationInput');
    await page.waitForSelector('#checkinInput');
    await page.waitForSelector('#checkoutInput');

    // Input search data
    await page.fill('#locationInput', 'New York');
    await page.fill('#checkinInput', '2023-12-01');
    await page.fill('#checkoutInput', '2023-12-10');

    // Click on the search button
    const searchButtonSelector = '.search-btn'; // Assuming a search button exists
    await page.waitForSelector(searchButtonSelector);
    await page.click(searchButtonSelector);

    // Wait for listings to load
    await page.waitForSelector('.listing-price');

    // Extract current prices
    const prices = await page.$$eval('.listing-price', elements => 
      elements.map(el => parseFloat(el.textContent.replace(/[^0-9.-]+/g,"")))
    );

    // Store prices in localStorage with a 10% discount applied
    const discountedPrices = prices.map(price => price * 0.9);
    await page.evaluate((discountedPrices) => {
      localStorage.setItem('discountedPrices', JSON.stringify(discountedPrices));
    }, discountedPrices);

    // Take a final screenshot after the operation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Extracted prices and stored discounted prices in localStorage",
      extractedData: { discountedPrices },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
