import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for listings to load
    await page.waitForSelector('.listing-card', { timeout: 10000 });

    // Extract all current prices
    const prices = await page.$$eval('.listing-price', elements => 
      elements.map(el => {
        const priceText = el.textContent.trim();
        const price = parseInt(priceText.replace(/[^\d]/g, ''));
        return { element: el.outerHTML, price, text: priceText };
      })
    );

    // Store original prices in localStorage
    await page.evaluate((prices) => {
      localStorage.setItem('originalPrices', JSON.stringify(prices));

      // Create pricing history if it doesn't exist
      if (!window.airbnbData) window.airbnbData = {};
      if (!window.airbnbData.pricingHistory) window.airbnbData.pricingHistory = {};

      prices.forEach((price, index) => {
        window.airbnbData.pricingHistory[`listing_${index}`] = {
          original: price.price,
          discounted: Math.round(price.price * 0.9),
          timestamp: new Date().toISOString()
        };
      });
    }, prices);

    // Apply 10% discount to all prices
    await page.$$eval('.listing-price', elements => {
      elements.forEach(el => {
        const priceText = el.textContent.trim();
        const price = parseInt(priceText.replace(/[^\d]/g, ''));
        const discounted = Math.round(price * 0.9);
        el.textContent = priceText.replace(price, discounted);
      });
    });

    // Take screenshot after price modification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_discounts_applied.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_discounts_applied.png'));

    // Verify changes were applied
    const updatedPrices = await page.$$eval('.listing-price', elements =>
      elements.map(el => parseInt(el.textContent.replace(/[^\d]/g, '')))
    );

    return {
      success: true,
      action: "Extracted all listing prices, stored in localStorage, and applied 10% discounts",
      extractedData: {
        originalPrices: prices.map(p => p.price),
        discountedPrices: updatedPrices,
        pricingHistory: await page.evaluate(() => window.airbnbData?.pricingHistory)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

